"""The purpose of this module is implement PEP 621 validations that are
difficult to express as a JSON Schema (or that are not supported by the current
JSON Schema library).
"""

from typing import Mapping, TypeVar

from .error_reporting import ValidationError

T = TypeVar("T", bound=Mapping)


class RedefiningStaticFieldAsDynamic(ValidationError):
    """According to PEP 621:

    Build back-ends MUST raise an error if the metadata specifies a field
    statically as well as being listed in dynamic.
    """


def validate_event_name_dynamic(pyevent_name: T) -> T:
    event_name_table = pyevent_name.get("event_name", {})
    dynamic = event_name_table.get("dynamic", [])

    for field in dynamic:
        if field in event_name_table:
            msg = f"You cannot provide a value for `event_name.{field}` and "
            msg += "list it under `event_name.dynamic` at the same time"
            name = f"data.event_name.{field}"
            value = {field: event_name_table[field], "...": " # ...", "dynamic": dynamic}
            raise RedefiningStaticFieldAsDynamic(msg, value, name, rule="PEP 621")

    return pyevent_name


EXTRA_VALIDATIONS = (validate_event_name_dynamic,)
